Конечно, вот решение для этой задачи на C++:

```cpp
#include <iostream>
#include <cmath>
using namespace std;

int main() {
    double a, b, angle;
    int choice;

    // Ввод данных
    cout << "Введите две стороны треугольника и угол между ними (в градусах): ";
    cin >> a >> b >> angle;

    // Перевод угла в радианы
    double angleRad = angle * M_PI / 180.0;

    // Меню
    cout << "Выберите действие:\n";
    cout << "1) Определить медианы\n";
    cout << "2) Определить третью сторону\n";
    cout << "3) Определить биссектрисы\n";
    cin >> choice;

    switch(choice) {
        case 1: {
            // Определение медиан
            double c = sqrt(a*a + b*b - 2*a*b*cos(angleRad)); // третья сторона
            double ma = sqrt(2*b*b + 2*c*c - a*a) / 2; // медиана к стороне a
            double mb = sqrt(2*a*a + 2*c*c - b*b) / 2; // медиана к стороне b
            double mc = sqrt(2*a*a + 2*b*b - c*c) / 2; // медиана к стороне c
            cout << "Медианы: ma = " << ma << ", mb = " << mb << ", mc = " << mc << endl;
            break;
        }
        case 2: {
            // Определение третьей стороны
            double c = sqrt(a*a + b*b - 2*a*b*cos(angleRad));
            cout << "Третья сторона: c = " << c << endl;
            break;
        }
        case 3: {
            // Определение биссектрис
            double c = sqrt(a*a + b*b - 2*a*b*cos(angleRad)); // третья сторона
            double p = (a + b + c) / 2; // полупериметр
            double la = 2 * sqrt(b*c*p*(p-a)) / (b+c); // биссектриса угла A
            double lb = 2 * sqrt(a*c*p*(p-b)) / (a+c); // биссектриса угла B
            double lc = 2 * sqrt(a*b*p*(p-c)) / (a+b); // биссектриса угла C
            cout << "Биссектрисы: la = " << la << ", lb = " << lb << ", lc = " << lc << endl;
            break;
        }
        default:
            cout << "Ошибка: неправильный выбор" << endl;
    }

    return 0;
}
```

