```jsx
import { useState } from 'react';

const AttachDocuments = () => {
  const [fileNames, setFileNames] = useState([]);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [isDragOver, setIsDragOver] = useState(false);

  const handleFileUploadChange = (e) => {
    const files = Array.from(e.target.files);
    setFileNames(files.map((file) => file.name));
    setSelectedFiles(files);
  };

  const handleDragEnter = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragOver(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragOver(false);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragOver(false);
    const files = Array.from(e.dataTransfer.files);
    setFileNames(files.map((file) => file.name));
    setSelectedFiles(files);
  };

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
      }}
    >
      <Typography variant="form_label">Attach Any Documents</Typography>
      <Box
        sx={{
          width: '100%',
          ml: '5px',
          border: 'none',
          borderRadius: '8px',
          bgcolor: 'white',
          height: '98px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          cursor: 'pointer',
          backgroundColor: isDragOver ? '#E3F2FD' : 'white',
        }}
        onDragEnter={handleDragEnter}
        onDragLeave={handleDragLeave}
        onDragOver={handleDragOver}
        onDrop={handleDrop}
        onClick={() => document.getElementById('fileElem')?.click()}
      >
        {fileNames.length > 0 ? (
          fileNames.map((fileName, index) => (
            <Typography key={index} sx={{ mr: 2 }}>
              {fileName}
            </Typography>
          ))
        ) : (
          <Typography
            sx={{
              fontWeight: '400',
              textAlign: 'center',
            }}
          >
            Drag, drop or <span style={{ color: '#1976d2' }}>choose a file</span> to upload
          </Typography>
        )}
        <input
          id="fileElem"
          name="file"
          hidden
          type="file"
          multiple
          onChange={handleFileUploadChange}
        />
      </Box>
    </Box>
  );
};

export default AttachDocuments;
```

