import android.graphics.Bitmap
import androidx.compose.foundation.Canvas
import androidx.compose.foundation.layout.Box
import androidx.compose.foundation.layout.size
import androidx.compose.runtime.Composable
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.*
import androidx.compose.ui.graphics.drawscope.DrawScope
import androidx.compose.ui.graphics.drawscope.rotate
import androidx.compose.ui.unit.dp
import com.google.zxing.BarcodeFormat
import com.google.zxing.qrcode.QRCodeWriter
import kotlin.random.Random

@Composable
fun ArtisticQRCode(content: String, size: Int = 300) {
    val qrCodeBitmap = generateQRCode(content, size).asImageBitmap()

    Box(modifier = Modifier.size(size.dp)) {
        Canvas(modifier = Modifier.matchParentSize()) {
            drawColorfulBackground()
            drawQRCodeWithEffects(qrCodeBitmap)
            drawArtisticElements()
        }
    }
}

fun DrawScope.drawColorfulBackground() {
    val gradient = Brush.radialGradient(
        colors = listOf(
            Color(0xFFFF9E80),
            Color(0xFFFF6E40),
            Color(0xFFFF3D00),
            Color(0xFFDD2C00)
        ),
        center = center,
        radius = size.maxDimension
    )
    drawRect(brush = gradient)

    drawCircle(
        brush = Brush.radialGradient(
            colors = listOf(Color(0x88FFFFFF), Color.Transparent),
            center = center,
            radius = size.maxDimension * 0.8f
        ),
        radius = size.maxDimension * 0.8f,
        blendMode = BlendMode.Overlay
    )
}

fun DrawScope.drawQRCodeWithEffects(qrBitmap: ImageBitmap) {
    // Draw "shadow" for 3D effect
    for (i in 10 downTo 1) {
        drawImage(
            image = qrBitmap,
            topLeft = Offset(i.toFloat(), i.toFloat()),
            alpha = 0.1f
        )
    }

    // Draw actual QR code
    drawImage(qrBitmap)

    // Add "shine" effect
    drawRect(
        brush = Brush.linearGradient(
            colors = listOf(Color(0x33FFFFFF), Color.Transparent),
            start = Offset.Zero,
            end = Offset(size.width, size.height)
        ),
        blendMode = BlendMode.Overlay
    )
}

fun DrawScope.drawArtisticElements() {
    // Draw swirls
    repeat(20) {
        rotate(Random.nextFloat() * 360f) {
            drawPath(
                path = Path().apply {
                    moveTo(0f, 0f)
                    cubicTo(
                        size.width * 0.3f, size.height * 0.3f,
                        size.width * 0.7f, size.height * 0.7f,
                        size.width, size.height
                    )
                },
                color = Color(
                    red = Random.nextFloat(),
                    green = Random.nextFloat(),
                    blue = Random.nextFloat(),
                    alpha = 0.3f
                ),
                style = Stroke(width = 5f)
            )
        }
    }

    // Draw splatters
    repeat(50) {
        drawCircle(
            color = Color(
                red = Random.nextFloat(),
                green = Random.nextFloat(),
                blue = Random.nextFloat(),
                alpha = 0.5f
            ),
            radius = Random.nextFloat() * 20f,
            center = Offset(Random.nextFloat() * size.width, Random.nextFloat() * size.height)
        )
    }
}

fun generateQRCode(content: String, size: Int): Bitmap {
    val writer = QRCodeWriter()
    val bitMatrix = writer.encode(content, BarcodeFormat.QR_CODE, size, size)
    val bitmap = Bitmap.createBitmap(size, size, Bitmap.Config.ARGB_8888)

    for (x in 0 until size) {
        for (y in 0 until size) {
            bitmap.setPixel(x, y, if (bitMatrix[x, y]) android.graphics.Color.BLACK else android.graphics.Color.WHITE)
        }
    }

    return bitmap
}
```

This merged version:

1. Combines the best parts of both code snippets.
2. Uses a `Box` composable to ensure proper sizing.
3. Implements a radial gradient for the background.
4. Applies a 3D-like effect to the QR code.
5. Adds artistic elements like swirls and splatters.
6. Uses random colors and rotations for a dynamic look.

The `ArtisticQRCode` composable can be used in your app like this:

```kotlin
@Composable
fun MyScreen() {
    ArtisticQRCode(content = "https://example.com", size = 300)
}
```

This code should create a visually appealing QR code with various artistic effects. Remember to test the scannability of the generated QR code, as the artistic elements might affect its readability.

