Here's the modified code with the changes you requested:

```matlab
clc; clear; close all;

%% System Generation
rng(42);
N = 1000;
u = rand(N,1);
y = zeros(size(u));

% System simulation
for i = 3:length(u)
    u_t1 = u(i-1);
    u_t2 = u(i-2);
    v_t = rand;    
    y(i) = 1.5*y(i-1) - 0.7*y(i-2) + u_t1 + 0.5*u_t2 + v_t;
end

% Plotting system response
figure('name','System','NumberTitle','off');
subplot(2,1,1);
plot(u,'LineWidth',1.5);
xlabel('Samples (N)'); ylabel('Input (Random)');
title('Input', 'FontSize', 20);
set(gca,'FontSize',18); grid on

subplot(2,1,2);
plot(y,'LineWidth',1.5);
xlabel('Samples (N)'); ylabel('Output (Y)');
title('System Response to Input', 'FontSize', 20);
set(gca,'FontSize',18); grid on

%% ARX 
data = iddata(y,u,1);
na = 2; % order of the AR part
nb = 2; % order of the B part
nk = 1; % delay
sys_arx = arx(data,[na nb nk]);

% Display the identified system parameters
disp('ARX System Parameters:');
disp(sys_arx);

% Simulate the system with the identified parameters
y_sim = lsim(sys_arx,u);

% Plot the actual and simulated system responses
figure('Name','ARX Model');
subplot(2,1,1);
plot(1:N, u, 'b','LineWidth',1.5);
title('Input Signal','FontSize',20);
xlabel('Samples (N)','FontSize',18); ylabel('Input','FontSize',18);
set(gca,'FontSize',18); grid on;

subplot(2,1,2);
plot(1:N, y, 'b', 1:N, y_sim, 'r','LineWidth',1.5);
legend('Actual', 'Simulated Arx');
title('System Response ARX Method','FontSize',20);
xlabel('Samples (N)','FontSize',18); ylabel('Output','FontSize',18);
set(gca,'FontSize',18); grid on;

%% Least-squares method
% Prepare data for least-squares method
y_ls = y(3:end);
phi = zeros(N-2, 4);
for i = 3:N
    phi(i-2, :) = [y(i-1), y(i-2), u(i-1), u(i-2)];
end

% Apply least-squares method
theta_LS = least_squares_method(y_ls, phi);

% Display the estimated parameters
disp('Least-squares estimated parameters:');
disp('[a1, a2, b1, b2]');
disp(theta_LS');

% Simulate the system with the estimated parameters
y_ls_sim = zeros(size(y));
for i = 3:N
    y_ls_sim(i) = theta_LS(1)*y_ls_sim(i-1) + theta_LS(2)*y_ls_sim(i-2) + ...
                  theta_LS(3)*u(i-1) + theta_LS(4)*u(i-2);
end

