%% Least-squares method function
function theta_ls = least_squares_method(y, phi)
    % y: vector of observed values
    % phi: matrix of regressors (each column is a regressor)

    N = length(y);

    % Compute the first term (matrix inverse)
    first_term = inv(1/N * phi' * phi);

    % Compute the second term
    second_term = 1/N * phi' * y;

    % Compute the least-squares estimate
    theta_ls = first_term * second_term;
end
