import { observer } from 'mobx-react';
import DonutChart from '../Components/DonutChart';
import BarChart from '../Components/BarChart';
import { useStore } from '../../hooks/useStore';

const JobMetricLandingPage = observer(() => {
  const { platformStore } = useStore();
  const [data, setData] = useState([]);
  const [jobStatus, setJobStatus] = useState([]);
  const [jobStatusCount, setJobStatusCount] = useState([]);
  const [optnTypeJobStatusCount, setOptnTypeJobStatusCount] = useState([]);
  const [appCodeJobStatusCount, setAppCodeJobStatusCount] = useState([]);
  const [selectedData, setSelectedData] = useState([]);
  const [updateFlag, setUpdateFlag] = useState(false);
  const [selectedDay, setSelectedDay] = useState('oneDay');
  const [alertFlag, setAlertFlag] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [donutSeries, setDonutSeries] = useState([]);
  const [donutLabels, setDonutLabels] = useState([]);
  const [barSeries, setBarSeries] = useState([]);
  const [barXaxis, setBarXaxis] = useState([]);
  const pageSize = 50;

  const dataFrom = [
    { day: 'Last 1 day', value: "oneDay" },
    { day: "Last 1 week", value: "oneWeek" }
  ];

  const getApiData = async (noOfDays, currentEnv, currentPlatform) => {
    try {
      setUpdateFlag(false);
      setAlertFlag(false);
      setAlertMessage('');

      const url = `${platformStore.baseUrl}getjobaudt?env=${currentEnv}&days=${noOfDays}&platform=${currentPlatform}`;
      const response = await fetch(url);

      if (!response.ok) throw new Error('Network response was not ok');

      const result = await response.json();

      if (result && result.jobStatusCount) {
        const donutCategories = result.jobStatusCount.map(item => item.job_stts);
        const donutCount = result.jobStatusCount.map(item => item.count);
        const barCount = result.optnTypeJobStatusCount.map(item => ({
          name: item.job_stts,
          data: Object.values(item.count)
        }));

        setDonutLabels(donutCategories);
        setDonutSeries(donutCount);
        setBarSeries(barCount);
