import java.text.ParseException;
import java.text.SimpleDateFormat;
import java.util.Date;
import java.util.Scanner;

public class Processor {
    private static PersonList personList;
    private static Scanner scanner = new Scanner(System.in);

    public Processor(PersonList personList) {
        this.personList = personList;
    }

    private static void addNewPerson() {
        System.out.println("Add a new person (S - Student, T - Teacher):");
        String type = scanner.next().toUpperCase();
        if ("S".equals(type)) {
            Student student = new Student("STUDENT1", "John Doe", new Date(), 3.5f, "Computer Science");
            personList.addPerson(student);
        } else if ("T".equals(type)) {
            Teacher teacher = new Teacher("TEACHER1", "Jane Doe", new Date(), "Mathematics", "Computer Science");
            personList.addPerson(teacher);
        }
    }

    private static void updatePerson() {
        System.out.println("Update a person by ID:");
        String id = scanner.next();
        Person person = personList.findPersonById(id);
        if (person != null) {
            System.out.println("Enter new values (leave empty to skip):");
            String newId = scanner.next();
            String newName = scanner.next();
            String newDateOfBirth = scanner.next();
            SimpleDateFormat sdf = new SimpleDateFormat("MM/dd/yyyy");
            Date dateOfBirth = null;
            try {
                dateOfBirth = sdf.parse(newDateOfBirth);
            } catch (ParseException e) {
                e.printStackTrace();
            }
            if (!newId.isEmpty()) {
                person.setId(newId);
            }
            if (!newName.isEmpty()) {
                person.setFullName(newName);
            }
            if (!dateOfBirth.toString().isEmpty()) {
                person.setDateOfBirth(dateOfBirth);
            }
            person.displayInfo();
        } else {
            System.out.println("Person not found.");
        }
    }

    private static void deletePersonById() {
        System.out.println("Delete a person by ID:");
        String id = scanner.next();
        personList.removePersonById(id);
    }

    private static void findPersonById() {
        System.out.println("Find a person by ID:");
        String id = scanner.next();
        Person person = personList.findPersonById(id);
        if (person != null) {
            person.displayInfo();
        } else {
            System.out.println("Person not found.");
        }
    }

    private static void displayEveryone() {
        personList.displayEveryone();
    }

    private static Student findTopStudent() {
        return personList.findTopStudent();
    }

    private static void findTeacherByDepartment() {
        System.out.println("Find a teacher by department:");
        String department = scanner.next();
        Teacher teacher = personList.findTeacherByDepartment(department);
        if (teacher != null) {
            teacher.displayInfo();
        } else {
            System.out.println("Teacher not found.");
        }
    }

    public static void main(String[] args) {
        PersonList personList = new PersonList(5);
        Processor processor = new Processor(personList);
        Student st = new Student("1234","Tran yen", "02/07/05", 9, "Khoa học máy tính");
        Student st1 = new Student("1235","Tran tran", "03/07/04", 10, "Ngon ngu");
        Teacher tc = new Teacher("0988", "Tran van A", "04/12/1995", "Khoa cong nghe", "Mon intoduction");
        Teacher tc1 = new Teacher("0948", "Tran van B", "04/11/1995", "Khoa Van", "Mon ngu van");
        personList.addPerson(st);
        personList.addPerson(st1);
        personList.addPerson(tc);
        personList.addPerson(tc1);
