                    break;
                case 6:
                    displayEveryone();
                    break;
                case 7:
                    findTopStudent();
                    break;
                case 8:
                    findTeacherByDepartment();
                    break;
                case 9:
                    System.out.println("Exiting...");
                    break;
                default:
                    System.out.println("Invalid option. Please try again.");
                    break;
            }
        }
    }
}
