class SimpleBot:
    # ... (previous methods remain the same)

    def is_vulnerable(self):
        total_defense = self.player.tower_height + self.player.fence_height
        return (total_defense < 35 or  # Vulnerable to Banshee (32 damage)
                self.player.tower_height < 25 or  # Potentially vulnerable to Dragon (25 damage)
                self.opponent.weapons >= total_defense)  # Vulnerable to direct attack

    def choose_action(self):
        playable_cards = self.get_playable_cards()

        if playable_cards:
            # Prioritize survival if vulnerable or under immediate threat
            if self.is_vulnerable() or self.is_under_immediate_threat():
                castle_cards = [i for i in playable_cards if "castle +" in self.player.cards[i].effect]
                fence_cards = [i for i in playable_cards if "fence +" in self.player.cards[i].effect]

                if castle_cards:
                    return "play", max(castle_cards, key=lambda i: int(self.player.cards[i].effect.split("+")[1].split()[0]))
                elif fence_cards:
                    return "play", max(fence_cards, key=lambda i: int(self.player.cards[i].effect.split("+")[1].split()[0]))

            # Increase resource generators if not vulnerable
            if not self.is_vulnerable():
                generator_cards = [i for i in playable_cards if any(x in self.player.cards[i].effect for x in ["builders +", "soldiers +", "mages +"])]
                if generator_cards:
                    return "play", random.choice(generator_cards)

            # Offensive strategy if we have a strong defense
            if self.player.tower_height > 50 and self.player.fence_height > 20:
                attack_cards = [i for i in playable_cards if "attack" in self.player.cards[i].effect or "enemy" in self.player.cards[i].effect]
                if attack_cards:
                    return "play", max(attack_cards, key=lambda i: int(self.player.cards[i].effect.split()[-1]))

            # Simple combo detection
            for i in playable_cards:
                card = self.player.cards[i]
                if "builders +1" in card.effect and self.player.bricks < 5:
                    return "play", i
                elif "soldiers +1" in card.effect and self.player.weapons < 5:
                    return "play", i
                elif "mages +1" in card.effect and self.player.crystals < 5:
                    return "play", i

            # Basic resource management
            resources = {'brick': self.player.bricks, 'sword': self.player.weapons, 'crystal': self.player.crystals}
