class FlipBook {
    constructor(bookElem) {
        this.elems = {
            book: bookElem,
            leaves: bookElem.querySelectorAll(".leaf"),
            buttons: {
                next: document.getElementById("nextPage"),
                prev: document.getElementById("prevPage")
            }
        };
        this.totalPages = this.elems.leaves.length;
        this.currentPage = 0;
        this.narrationEnabled = false;
        this.speech = new SpeechSynthesisUtterance();
        this.speech.rate = 0.7;
        this.speech.pitch = 0.9;

        this.init();
    }

    init() {
        this.setupPages();
        this.setupEvents();
        this.updateButtonStates();
        this.setupAdditionalEvents();
        this.setupSearch();
        this.setupTableOfContents();
        this.setupImageZoom();
        this.setupNarration();
        this.setupKeyboardNavigation();
        this.setupSwipeGestures();
        this.enhanceSearchFunctionality();
        document.getElementById('loading').style.display = 'none';
    }

    setupPages() {
        this.elems.leaves.forEach((leaf, index) => {
            leaf.style.zIndex = this.totalPages - index;
            if (index !== 0) {
                leaf.classList.add('flipped');
            }
        });
    }

    setupEvents() {
        this.elems.buttons.next.addEventListener("click", () => this.turnPage(1));
        this.elems.buttons.prev.addEventListener("click", () => this.turnPage(-1));

        this.elems.leaves.forEach((leaf, index) => {
            leaf.addEventListener('click', (event) => {
                const rect = leaf.getBoundingClientRect();
                const x = event.clientX - rect.left;
                if (x < rect.width / 2) {
                    this.turnPage(-1);
                } else {
                    this.turnPage(1);
                }
            });
        });
    }

    turnPage(direction) {
        if (direction > 0 && this.currentPage < this.totalPages - 1) {
            this.elems.leaves[this.currentPage].classList.add('flipped');
            this.currentPage++;
        } else if (direction < 0 && this.currentPage > 0) {
            this.currentPage--;
            this.elems.leaves[this.currentPage].classList.remove('flipped');
        }

        this.updateButtonStates();
        this.narratePage();
    }

    updateButtonStates() {
        this.elems.buttons.prev.disabled = (this.currentPage === 0);
        this.elems.buttons.next.disabled = (this.currentPage === this.totalPages - 1);
    }

    setupAdditionalEvents() {
        document.getElementById('darkModeToggle').addEventListener('click', () => {
            document.body.classList.toggle('dark-mode');
        });
    }

    setupSearch() {
        const searchInput = document.getElementById('searchInput');
        const searchButton = document.getElementById('searchButton');

        const performSearch = () => {
            const searchTerm = searchInput.value.toLowerCase();
            this.searchPages(searchTerm);
        };

        searchButton.addEventListener('click', performSearch);
        searchInput.addEventListener('keypress', (e) => {
            if (e.key === 'Enter') performSearch();
        });
    }

    searchPages(term) {
        const foundIndex = Array.from(this.elems.leaves).findIndex(leaf => 
            leaf.textContent.toLowerCase().includes(term)
        );

        if (foundIndex !== -1) {
            this.goToPage(foundIndex);
        } else {
            alert('Term not found');
        }
    }

    setupTableOfContents() {
        const tocDropdown = document.getElementById('tocDropdown');
        this.elems.leaves.forEach((leaf, index) => {
            const option = document.createElement('option');
            option.value = index;
            option.textContent = `Page ${index + 1}`;
            tocDropdown.appendChild(option);
        });

        tocDropdown.addEventListener('change', (e) => {
            const pageNumber = parseInt(e.target.value);
            if (!isNaN(pageNumber)) {
                this.goToPage(pageNumber);
            }
        });

        document.querySelectorAll('.table-of-contents a').forEach(link => {
            link.addEventListener('click', (e) => {
                e.preventDefault();
                this.goToPage(parseInt(link.getAttribute('data-page')));
            });
        });
    }

    goToPage(pageNumber) {
        while (this.currentPage < pageNumber) {
            this.turnPage(1);
        }
        while (this.currentPage > pageNumber) {
            this.turnPage(-1);
        }
    }

    setupImageZoom() {
        this.elems.book.querySelectorAll('img').forEach(img => {
            img.classList.add('zoomable');
            img.addEventListener('click', (e) => {
                e.stopPropagation();
                img.classList.toggle('zoomed');
            });
        });
    }

    setupNarration() {
        const toggleButton = document.getElementById('toggleNarration');
        toggleButton.addEventListener('click', () => {
            this.narrationEnabled = !this.narrationEnabled;
            toggleButton.textContent = this.narrationEnabled ? 'Disable Narration' : 'Enable Narration';
            if (this.narrationEnabled) {
                this.narratePage();
            } else {
                window.speechSynthesis.cancel();
            }
