        window.speechSynthesis.onvoiceschanged = () => {
            const voices = window.speechSynthesis.getVoices();
            const preferredVoice = voices.find(voice => voice.name.includes('Samantha') || voice.name.includes('Calm') || voice.name.includes('Soft'));
            if (preferredVoice) {
                this.speech.voice = preferredVoice;
            }
        };
    }

    narratePage() {
        if (!this.narrationEnabled) return;

        const pageContent = this.elems.leaves[this.currentPage].querySelector('.page:not(.back)');
        if (pageContent) {
