            height = self.column_height(grid, col)
            max_height = max(max_height, height)

        for row in grid:
            if 0 in row:
                holes += 1
            if all(cell != 0 for cell in row):
                completed_lines += 1

        # Prioritize low tower height with exponential penalty for approaching the top
        base_score = 0
        if max_height == 0:
            base_score = 0
        elif max_height == 1:
