        elif max_height == 19:
            base_score = -25
        elif max_height == 20:
            base_score = 0
        elif max_height > 20:
            base_score = (max_height - 20) * 10 + 0

        # Second priority: Line clearing (significant boost) - Prioritize single lines
        base_score += completed_lines * 5000 

        # Third priority: Hole avoidance (less emphasis)
        base_score += holes * 100 

        return base_score

    def computer_move(self, grid, block):
        possible_moves = []
        for x in range(self.grid_width):
            temp_grid = [row[:] for row in grid]
            temp_block = Block(block.shape, block.color, x, block.y)

            drop_y = temp_block.y
            while not self.check_collision(temp_grid, temp_block, temp_block.x, drop_y + 1):
