        max_height = max(heights)
        avg_height = sum(heights) / len(heights)
        height_variance = sum((h - avg_height) ** 2 for h in heights) / len(heights)
        holes = self.count_holes(test_grid)
        complete_lines = sum(all(cell != 0 for cell in row) for row in test_grid)

        # Evaluate the move based on multiple factors
