            edge_bonus = 20  # Slight bonus for using edges

        well_score = self.evaluate_well(test_grid) * 10

        return height_score + variance_score + hole_score + line_score + edge_bonus + well_score

    def evaluate_well(self, grid):
        well_score = 0
        for col in range(GAME_WIDTH):
            if col == 0 or col == GAME_WIDTH - 1:
                continue
            well_depth = 0
            for row in range(GAME_HEIGHT - 1, -1, -1):
                if grid[row][col] == 0 and grid[row][col-1] != 0 and grid[row][col+1] != 0:
                    well_depth += 1
                else:
                    break
            well_score += well_depth ** 2  # Quadratic scoring for deeper wells
        return well_score

    def count_holes(self, grid):
        holes = 0
        for col in range(GAME_WIDTH):
            block_found = False
            for row in range(GAME_HEIGHT):
                if grid[row][col] != 0:
                    block_found = True
                elif block_found:
                    holes += 1
        return holes

